mapper.select:
select DISTINCT d.project_id, d.project_name, DATE_FORMAT(d.create_date,"%Y-%m-%d") as createDate from daily d inner join daily_project dp on d.project_id = dp.id where DATE_FORMAT(d.create_date,"%Y-%m-%d") in
#{item.date}
order by dp.sorts desc

select DISTINCT d.id, d.project_id, d.post_code, d.content, DATE_FORMAT(d.create_date,"%Y-%m-%d") as createDate from daily d inner join position p on d.post_code = p.code where DATE_FORMAT(d.create_date,"%Y-%m-%d") in
#{item.date}
order by p.sorts
